import React,{useState} from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

const NewExpense=(props)=>{
    const [isEditing,setIsEditing] =useState(false);
    const saveExpenseDataHandler =(enteredExpenseData)=>{
        const expenseData ={
            //taking all the previous state values
            ...enteredExpenseData,
            //adding the new value to the current state
            id: Math.random().toString()
        };

        //passing the whole modified object with id.
        props.onAddExpense(expenseData);
    };
    const startEditingHandler =()=>{
        setIsEditing(true);
    };
    const stopEditingHandler =()=>{
        setIsEditing(false);
    }

    return (
        <div className="new-expense">
            {!isEditing && <button onClick={startEditingHandler}>Add a new Expense</button>}
           {isEditing && <ExpenseForm onSaveExpenseData ={saveExpenseDataHandler} onCancle ={stopEditingHandler}></ExpenseForm>}
        </div>
    );
}
export default NewExpense;
